select FirstName, MiddleName, LastName, EmailID, DateOfBirth, sum(Quantity*(Food_Price - Food_Actual_Price)) as ActualBill, 0.8*sum(Quantity*(Food_Price - Food_Actual_Price)) as DiscountedBill from customer_details, customer_record, food_items where month(curdate()) = month(DateOfBirth) and dayofmonth(curdate()) = dayofmonth(DateOfBirth) and customer_record.Customer_ID = customer_details.Customer_ID and customer_record.Food_ID = food_items.Food_ID group by customer_record.Customer_ID;